import React, { useState, useEffect, useRef } from 'react';
import {
  Upload,
  Send,
  FileText,
  Trash2,
  MessageSquare,
  Brain,
  Loader2,
  BarChart3,
  FileSearch,
  Globe,
  TrendingUp,
  Clock,
  Zap
} from 'lucide-react';

const STORAGE_KEYS = {
  DOCUMENTS: 'rag_documents',
  CHAT_HISTORY: 'rag_chat_history',
  ANALYTICS: 'rag_analytics'
};

const LANGUAGES = [
  { code: 'en', name: 'English', flag: 'ðŸ‡¬ðŸ‡§' },
  { code: 'es', name: 'Spanish', flag: 'ðŸ‡ªðŸ‡¸' },
  { code: 'fr', name: 'French', flag: 'ðŸ‡«ðŸ‡·' },
  { code: 'de', name: 'German', flag: 'ðŸ‡©ðŸ‡ª' },
  { code: 'hi', name: 'Hindi', flag: 'ðŸ‡®ðŸ‡³' },
  { code: 'zh', name: 'Chinese', flag: 'ðŸ‡¨ðŸ‡³' },
  { code: 'ja', name: 'Japanese', flag: 'ðŸ‡¯ðŸ‡µ' }
];

export default function EnterpriseKnowledgeAssistant() {
  const [documents, setDocuments] = useState([]);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [activeTab, setActiveTab] = useState('chat');
  const [uploadProgress, setUploadProgress] = useState(null);
  const [selectedLanguage, setSelectedLanguage] = useState('en');
  const [analytics, setAnalytics] = useState({
    totalQueries: 0,
    avgResponseTime: 0,
    topQuestions: [],
    documentsUsed: {}
  });
  const [showSummary, setShowSummary] = useState(false);
  const [summaryDoc, setSummaryDoc] = useState(null);
  const [isSummarizing, setIsSummarizing] = useState(false);
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);

  useEffect(() => {
    loadFromStorage();
    // also fetch server-side documents if backend already has some
    fetchServerDocuments();
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // storage helpers: try window.storage if available, otherwise localStorage
  const storageGet = async (key) => {
    try {
      if (window.storage && window.storage.get) {
        return await window.storage.get(key);
      } else {
        const v = localStorage.getItem(key);
        return v ? { value: v } : null;
      }
    } catch (e) {
      return null;
    }
  };

  const storageSet = async (key, value) => {
    try {
      if (window.storage && window.storage.set) {
        await window.storage.set(key, value);
      } else {
        localStorage.setItem(key, value);
      }
    } catch (e) {
      console.error('Storage set error', e);
    }
  };

  const loadFromStorage = async () => {
    try {
      const docs = await storageGet(STORAGE_KEYS.DOCUMENTS);
      const history = await storageGet(STORAGE_KEYS.CHAT_HISTORY);
      const analyticsData = await storageGet(STORAGE_KEYS.ANALYTICS);

      if (docs) setDocuments(JSON.parse(docs.value || docs));
      if (history) setMessages(JSON.parse(history.value || history));
      if (analyticsData) setAnalytics(JSON.parse(analyticsData.value || analyticsData));
    } catch (error) {
      console.log('No existing data found', error);
    }
  };

  const saveToStorage = async (key, data) => {
    try {
      await storageSet(key, JSON.stringify(data));
    } catch (error) {
      console.error('Storage error:', error);
    }
  };

  const updateAnalytics = (query, sources, responseTime) => {
    setAnalytics(prev => {
      const newAnalytics = {
        totalQueries: prev.totalQueries + 1,
        avgResponseTime:
          ((prev.avgResponseTime * prev.totalQueries) + responseTime) / (prev.totalQueries + 1),
        topQuestions: [...prev.topQuestions, { query, timestamp: Date.now() }].slice(-20),
        documentsUsed: { ...prev.documentsUsed }
      };

      sources.forEach(source => {
        newAnalytics.documentsUsed[source] = (newAnalytics.documentsUsed[source] || 0) + 1;
      });

      saveToStorage(STORAGE_KEYS.ANALYTICS, newAnalytics);
      return newAnalytics;
    });
  };

  // Local chunking and semantic search (used for client-side preview; server-side search uses server docs)
  const chunkText = (text, chunkSize) => {
    const chunks = [];
    const sentences = text.match(/[^.!?]+[.!?]+/g) || [text];
    let currentChunk = '';

    for (const sentence of sentences) {
      if ((currentChunk + sentence).length > chunkSize && currentChunk) {
        chunks.push(currentChunk.trim());
        currentChunk = sentence;
      } else {
        currentChunk += sentence;
      }
    }
    if (currentChunk) chunks.push(currentChunk.trim());
    return chunks;
  };

  const semanticSearch = (query, topK = 5) => {
    const allChunks = documents.flatMap(doc =>
      doc.chunks.map(chunk => ({ chunk, docName: doc.name, docId: doc.id }))
    );

    const queryWords = query.toLowerCase().split(/\s+/);

    const scored = allChunks.map(item => {
      const chunkLower = item.chunk.toLowerCase();
      let score = 0;

      queryWords.forEach(word => {
        const count = (chunkLower.match(new RegExp(word, 'g')) || []).length;
        score += count;
      });

      return { ...item, score };
    });

    return scored
      .filter(item => item.score > 0)
      .sort((a, b) => b.score - a.score)
      .slice(0, topK);
  };

  // fetch server-side documents list
  const fetchServerDocuments = async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/documents');
      if (!res.ok) return;
      const data = await res.json();
      if (data.documents) {
        // map server docs into the same shape as local docs
        const serverDocs = data.documents.map(d => ({
          id: d.id,
          name: d.name,
          type: 'text/plain',
          uploadDate: d.upload_date || d.uploadDate || new Date().toISOString(),
          content: d.content,
          chunks: d.chunks || chunkText(d.content || '', 1000),
          chunkCount: d.chunk_count || (d.chunks ? d.chunks.length : 0),
          summary: d.summary || null
        }));
        // merge: keep local documents too, but prefer server versions if ids match
        const merged = [...serverDocs];
        documents.forEach(localDoc => {
          if (!merged.find(md => md.id === localDoc.id)) merged.push(localDoc);
        });
        setDocuments(merged);
        saveToStorage(STORAGE_KEYS.DOCUMENTS, merged);
      }
    } catch (e) {
      // ignore silently
    }
  };

  // upload file(s) to backend
  const handleFileUpload = async (e) => {
    const files = Array.from(e.target.files);
    if (files.length === 0) return;

    setUploadProgress({ current: 0, total: files.length });

    for (let i = 0; i < files.length; i++) {
      const file = files[i];

      // only allow text files for now (backend supports .txt, .md, .csv)
      if (!file.name.match(/\.(txt|md|csv)$/i)) {
        alert('Only text files (.txt, .md, .csv) are supported for upload.');
        setUploadProgress(null);
        return;
      }

      try {
        const form = new FormData();
        form.append('file', file, file.name);

        const res = await fetch('http://127.0.0.1:8000/upload', {
          method: 'POST',
          body: form
        });

        if (!res.ok) {
          const err = await res.json().catch(() => null);
          console.error('Upload failed', err);
          alert('Upload failed: ' + (err?.detail || res.statusText));
        } else {
          const data = await res.json();
          // After upload, refresh server doc list
          await fetchServerDocuments();
        }

        setUploadProgress({ current: i + 1, total: files.length });
      } catch (error) {
        console.error('Error uploading file:', error);
        alert('Error uploading file: ' + error.message);
      }
    }

    setTimeout(() => setUploadProgress(null), 1500);
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  const extractTextFromFile = async (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (e) => {
        const text = e.target.result;
        resolve(text);
      };
      reader.onerror = reject;
      reader.readAsText(file);
    });
  };

  // request server to summarize a document
  const generateSummary = async (docId) => {
    setIsSummarizing(true);
    setSummaryDoc(docId);

    try {
      const res = await fetch('http://127.0.0.1:8000/summarize', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ document_id: docId })
      });

      if (!res.ok) {
        const err = await res.json().catch(() => null);
        throw new Error(err?.detail || 'Summarize failed');
      }

      const data = await res.json();
      const summary = data.summary || data?.result || 'No summary returned';

      // update in-memory and local storage
      setDocuments(prev => {
        const updated = prev.map(d => d.id === docId ? { ...d, summary } : d);
        saveToStorage(STORAGE_KEYS.DOCUMENTS, updated);
        return updated;
      });

      setShowSummary(true);
    } catch (error) {
      console.error('Error generating summary:', error);
      alert('Failed to generate summary. Please try again.');
    } finally {
      setIsSummarizing(false);
    }
  };

  // send user query to server (server will perform semantic search + OpenAI call)
  const handleSendMessage = async () => {
    if (!input.trim() || isProcessing) return;
    // if there are no documents on server and no local documents, warn
    if (documents.length === 0) {
      alert('Please upload documents first!');
      return;
    }

    const startTime = Date.now();
    const userMessage = { role: 'user', content: input, timestamp: new Date().toISOString() };
    setMessages(prev => [...prev, userMessage]);
    const currentQuery = input;
    setInput('');
    setIsProcessing(true);

    try {
      const payload = {
        query: currentQuery,
        language: selectedLanguage,
        session_id: `session_${Date.now()}`
      };

      const res = await fetch('http://127.0.0.1:8000/query', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });

      if (!res.ok) {
        const err = await res.json().catch(() => null);
        throw new Error(err?.detail || 'Query failed');
      }

      const data = await res.json();
      const assistantText = data.response || data.answer || 'No response from AI.';
      const sources = data.sources || [];
      const responseTime = data.response_time ? data.response_time * 1000 : (Date.now() - startTime);

      const assistantMessage = {
        role: 'assistant',
        content: assistantText,
        sources,
        timestamp: new Date().toISOString(),
        responseTime
      };

      setMessages(prev => {
        const updated = [...prev, assistantMessage];
        saveToStorage(STORAGE_KEYS.CHAT_HISTORY, updated);
        return updated;
      });

      updateAnalytics(currentQuery, sources, responseTime);
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = {
        role: 'assistant',
        content: 'Sorry, I encountered an error processing your request. Please try again.',
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsProcessing(false);
    }
  };

  const deleteDocument = (docId) => {
    // Remove locally; server-side deletion endpoint exists but not used here
    setDocuments(prev => {
      const updated = prev.filter(doc => doc.id !== docId);
      saveToStorage(STORAGE_KEYS.DOCUMENTS, updated);
      return updated;
    });
  };

  const clearChat = () => {
    setMessages([]);
    saveToStorage(STORAGE_KEYS.CHAT_HISTORY, []);
  };

  const getMostUsedDocs = () => {
    return Object.entries(analytics.documentsUsed)
      .sort(([, a], [, b]) => b - a)
      .slice(0, 5);
  };

  return (
    <div className="min-h-screen">
      <div className="app">
        <div className="container">
          {/* Header */}
          <div className="header">
            <div>
              <h1>Enterprise Knowledge Assistant</h1>
              <p>Powered by retrieval-augmented generation â€¢ Multi-language support</p>
            </div>

            {/* Language Selector */}
            <div className="language-selector">
              <select
                value={selectedLanguage}
                onChange={(e) => setSelectedLanguage(e.target.value)}
                aria-label="Select language"
              >
                {LANGUAGES.map(lang => (
                  <option key={lang.code} value={lang.code}>
                    {lang.flag} {lang.name}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Tabs */}
          <div className="tabs">
            <button
              onClick={() => setActiveTab('chat')}
              className={activeTab === 'chat' ? 'active' : ''}
            >
              <MessageSquare className="w-4 h-4 inline mr-2" />
              Chat
            </button>
            <button
              onClick={() => setActiveTab('documents')}
              className={activeTab === 'documents' ? 'active' : ''}
            >
              <FileText className="w-4 h-4 inline mr-2" />
              Documents ({documents.length})
            </button>
            <button
              onClick={() => setActiveTab('analytics')}
              className={activeTab === 'analytics' ? 'active' : ''}
            >
              <BarChart3 className="w-4 h-4 inline mr-2" />
              Analytics
            </button>
          </div>

          {/* Content */}
          <div className="content">
            {activeTab === 'chat' ? (
              <div className="chat-container">
                {/* Chat Header */}
                <div className="chat-header">
                  <div>
                    {documents.length} document{documents.length !== 1 ? 's' : ''} â€¢ Language: {LANGUAGES.find(l => l.code === selectedLanguage)?.flag}
                  </div>
                  {messages.length > 0 && (
                    <button
                      onClick={clearChat}
                      className="text-accent-primary hover:text-accent-secondary"
                      title="Clear chat history"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                  )}
                </div>

                {/* Messages */}
                <div className="messages">
                  {messages.length === 0 ? (
                    <div className="empty-state">
                      <Brain className="w-12 h-12 opacity-30" />
                      <h3 style={{ fontSize: '16px', fontWeight: '600' }}>
                        Ready to Assist
                      </h3>
                      <p style={{ fontSize: '13px', maxWidth: '320px' }}>
                        Upload documents and ask questions. Answers will be sourced from your knowledge base.
                      </p>
                    </div>
                  ) : (
                    messages.map((msg, idx) => (
                      <div key={idx} className={`message ${msg.role}`}>
                        <div className="message-content">
                          <div>{msg.content}</div>
                          {msg.sources && msg.sources.length > 0 && (
                            <div className="sources">
                              {msg.sources.map((source, i) => (
                                <span key={i} className="source-tag">
                                  {source}
                                </span>
                              ))}
                            </div>
                          )}
                          {msg.responseTime && (
                            <div className="response-time">
                              <Zap className="w-3 h-3" />
                              <span>{(msg.responseTime / 1000).toFixed(2)}s</span>
                            </div>
                          )}
                        </div>
                      </div>
                    ))
                  )}
                  <div ref={messagesEndRef} />
                </div>

                {/* Input Area */}
                <div className="input-area">
                  <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                    placeholder={`Ask in ${LANGUAGES.find(l => l.code === selectedLanguage)?.name}...`}
                    disabled={isProcessing || documents.length === 0}
                  />
                  <button
                    onClick={handleSendMessage}
                    disabled={isProcessing || !input.trim() || documents.length === 0}
                    title="Send message"
                  >
                    {isProcessing ? <Loader2 className="w-4 h-4 animate-spin" /> : <Send className="w-4 h-4" />}
                  </button>
                </div>
              </div>
            ) : activeTab === 'documents' ? (
              <div style={{ padding: 0 }}>
                {/* Upload Section */}
                <div style={{ marginBottom: 'var(--spacing-2xl)' }}>
                  <input
                    ref={fileInputRef}
                    type="file"
                    multiple
                    accept=".txt,.md,.csv"
                    onChange={handleFileUpload}
                    className="hidden"
                    id="file-upload"
                  />
                  <label
                    htmlFor="file-upload"
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      gap: 'var(--spacing-md)',
                      background: 'linear-gradient(180deg, rgba(124, 58, 237, 0.96), rgba(109, 40, 217, 0.90))',
                      color: 'white',
                      padding: 'var(--spacing-lg) var(--spacing-2xl)',
                      borderRadius: 'var(--radius-md)',
                      cursor: 'pointer',
                      transition: 'all var(--transition-base)',
                      border: '1px solid rgba(255, 255, 255, 0.08)',
                    }}
                    className="hover:shadow-md"
                  >
                    <Upload className="w-4 h-4" />
                    <span style={{ fontWeight: '600', fontSize: '14px' }}>Upload Documents</span>
                  </label>
                  {uploadProgress && (
                    <div style={{ marginTop: 'var(--spacing-lg)', padding: 'var(--spacing-lg)', borderRadius: 'var(--radius-md)', background: 'rgba(255, 255, 255, 0.80)', border: '1px solid var(--border-primary)' }}>
                      <div style={{ fontSize: '13px', color: 'var(--text-primary)', marginBottom: 'var(--spacing-md)', fontWeight: '500' }}>
                        Uploading {uploadProgress.current} of {uploadProgress.total}...
                      </div>
                      <div style={{ width: '100%', height: '4px', borderRadius: '2px', background: 'var(--accent-light)', overflow: 'hidden' }}>
                        <div
                          style={{
                            height: '100%',
                            background: 'var(--accent-primary)',
                            transition: 'width var(--transition-base)',
                            width: `${(uploadProgress.current / uploadProgress.total) * 100}%`
                          }}
                        />
                      </div>
                    </div>
                  )}
                </div>

                {/* Documents List */}
                <div className="documents-container">
                  {documents.length === 0 ? (
                    <div className="empty-state">
                      <FileText className="w-10 h-10 opacity-30" />
                      <p style={{ fontSize: '13px' }}>No documents uploaded</p>
                    </div>
                  ) : (
                    documents.map(doc => (
                      <div key={doc.id} className="document-card">
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                          <div style={{ display: 'flex', gap: 'var(--spacing-md)', flex: 1 }}>
                            <FileText className="w-4 h-4" style={{ marginTop: '2px', color: 'var(--accent-primary)', flexShrink: 0 }} />
                            <div style={{ flex: 1 }}>
                              <div style={{ fontWeight: '600', fontSize: '14px', color: 'var(--text-primary)' }}>{doc.name}</div>
                              <div style={{ fontSize: '12px', color: 'var(--text-tertiary)', marginTop: 'var(--spacing-sm)' }}>
                                {doc.chunkCount} chunks â€¢ {new Date(doc.uploadDate).toLocaleDateString()}
                              </div>
                            </div>
                          </div>
                          <div className="doc-actions" style={{ display: 'flex', gap: 'var(--spacing-md)', marginLeft: 'var(--spacing-lg)' }}>
                            <button
                              onClick={() => generateSummary(doc.id)}
                              disabled={isSummarizing && summaryDoc === doc.id}
                              title="Generate Summary"
                              style={{ color: 'var(--accent-primary)' }}
                            >
                              {isSummarizing && summaryDoc === doc.id ? (
                                <Loader2 className="w-4 h-4 animate-spin" />
                              ) : (
                                <FileSearch className="w-4 h-4" />
                              )}
                            </button>
                            <button
                              onClick={() => deleteDocument(doc.id)}
                              title="Delete document"
                              style={{ color: '#ef4444' }}
                            >
                              <Trash2 className="w-4 h-4" />
                            </button>
                          </div>
                        </div>

                      {doc.summary && (
                        <div className="mt-3 pt-3 border-t border-slate-600">
                          <div className="text-xs text-purple-400 mb-2 font-semibold">AI Summary:</div>
                          <div className="text-sm text-gray-300 whitespace-pre-wrap">{doc.summary}</div>
                        </div>
                      )}
                    </div>
                  ))
                )}
              </div>
            </div>
          ) : (
            <div className="p-6">
              <h2 className="text-2xl font-bold mb-6 flex items-center gap-2">
                <TrendingUp className="w-6 h-6 text-purple-400" />
                Query Analytics
              </h2>

              {/* Stats Grid */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
                <div className="bg-slate-700/50 rounded-lg p-4">
                  <div className="text-sm text-gray-400 mb-1">Total Queries</div>
                  <div className="text-3xl font-bold text-purple-400">{analytics.totalQueries}</div>
                </div>
                <div className="bg-slate-700/50 rounded-lg p-4">
                  <div className="text-sm text-gray-400 mb-1">Avg Response Time</div>
                  <div className="text-3xl font-bold text-green-400">
                    {(analytics.avgResponseTime / 1000).toFixed(2)}s
                  </div>
                </div>
                <div className="bg-slate-700/50 rounded-lg p-4">
                  <div className="text-sm text-gray-400 mb-1">Documents Indexed</div>
                  <div className="text-3xl font-bold text-blue-400">{documents.length}</div>
                </div>
              </div>

              {/* Most Used Documents */}
              {getMostUsedDocs().length > 0 && (
                <div className="mb-8">
                  <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
                    <FileText className="w-5 h-5 text-purple-400" />
                    Most Referenced Documents
                  </h3>
                  <div className="space-y-2">
                    {getMostUsedDocs().map(([docName, count]) => (
                      <div key={docName} className="bg-slate-700/50 rounded-lg p-3 flex justify-between items-center">
                        <span className="text-gray-300">{docName}</span>
                        <span className="text-purple-400 font-semibold">{count} references</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Recent Questions */}
              {analytics.topQuestions.length > 0 && (
                <div>
                  <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
                    <Clock className="w-5 h-5 text-purple-400" />
                    Recent Questions
                  </h3>
                  <div className="space-y-2">
                    {analytics.topQuestions.slice(-10).reverse().map((item, idx) => (
                      <div key={idx} className="bg-slate-700/50 rounded-lg p-3">
                        <div className="text-gray-300">{item.query}</div>
                        <div className="text-xs text-gray-500 mt-1">
                          {new Date(item.timestamp).toLocaleString()}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {analytics.totalQueries === 0 && (
                <div className="text-center py-12 text-gray-500">
                  <BarChart3 className="w-12 h-12 mx-auto mb-3 opacity-50" />
                  <p>No analytics data yet. Start asking questions!</p>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Info Footer */}
        <div className="mt-6 text-center text-sm text-gray-500">
          <p>RAG Architecture â€¢ Multi-language â€¢ Analytics Dashboard</p>
        </div>
        </div>
      </div>
    </div>
  );
}
